<?php include_once( 'qq/config.php' );include_once( 'qq/txwboauth.php' );date_default_timezone_set('Asia/Chongqing');class qq{		var $loginUrl;		function getUrl( $call_back = null, $needSkip, $uid, $isPick ) {		$o = new QqWeiboOAuth( QQWB_AKEY , QQWB_SKEY  );        		if ( is_null($call_back) ) {			if ( $isPick ) {				$call_back = U( 'home/Public/PickCallback', array( 'type' => 'qq' ) );			}			else {				if ($needSkip == 1) {					$call_back = U( 'home/Public/qqcallback', array( 'skip' => 1, 'newid' => $uid ) );				}				else {					$call_back = U('home/Public/qqcallback');				}			}		}				$keys = $o->getRequestToken( $call_back );		$this->loginUrl = $o->getAuthorizeURL( $keys['oauth_token'] ,false , "");		$_SESSION['qq']['keys'] = $keys;		return $this->loginUrl;	}//用户资料	function userInfo(){		$me = $this->doClient()->verify_credentials();		$user['id'] 				= $me['data']['name'];		$user['uname'] 		= $me['data']['nick'];		$user['province'] 	= $me['data']['province_code'];		$user['city'] 			= $me['data']['city_code'];		$user['location'] 	= $me['data']['location'];		$user['userface'] 	= $me['data']['head'];		$user['sex'] 			= ($me['data']['sex']=='1') ? 1 : 0;				//print_r($user);		return $user;	}		private function doClient($opt){        		$oauth_token = ( $opt['oauth_token'] )? $opt['oauth_token']:$_SESSION['qq']['access_token']['oauth_token'];		$oauth_token_secret = ( $opt['oauth_token_secret'] )? $opt['oauth_token_secret']:$_SESSION['qq']['access_token']['oauth_token_secret'];                		return new QqWeiboClient( QQWB_AKEY , QQWB_SKEY ,  $oauth_token, $oauth_token_secret  );	}		function user($opt)	{		return $this->doClient($opt)->user_info();	}	//验证用户	function checkUser(){		$o = new QqWeiboOAuth( QQWB_AKEY , QQWB_SKEY , $_SESSION['qq']['keys']['oauth_token'] , $_SESSION['qq']['keys']['oauth_token_secret']  );		$access_token = $o->getAccessToken(  $_REQUEST['oauth_verifier'] ) ;		$_SESSION['qq']['access_token'] = $access_token;		$_SESSION['open_platform_type'] = 'qq';	}		//发布一条微博	function update($text,$opt){            //print_r($this->doClient($opt));           // print_r($this->doClient($opt)->t_add($text));		return $this->doClient($opt)->t_add($text);	}		//上传一个照片，并发布一条微博	function upload($text,$pic,$opt){		$pic = file_get_contents( getImgRealPath($pic) );		$data = $this->doClient($opt)->t_add_pic($text, $pic);		$errcode = array('10','13','75' );				if( (in_array($data['errcode'], $errcode) && $data['ret'] == 4) || $data['ret'] == 2 || $data['ret'] == 3046){			return true;		}		else if ( $data['errcode'] != 0 ){			return false;		}		return true;	}		/**	 * 获取好友	 */	function get_fanslist() {		$fans = $this->doClient()->f_fanslist();				if ($fans && $fans['ret'] == 0) {			foreach ( $fans['data']['info'] as $key => $value ) {				$user[$key]['uname']       = $value['name'];				$user[$key]['userface']    = $value['head']."/50";			}		}		return $user;	}		function test() {		return 'qq';	}}?>